mapper.update:
update category
set user_id = #{userId,jdbcType=BIGINT},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, user_id, name
from category
where id = #{id,jdbcType=BIGINT}

select id, user_id, name
from category
where user_id = #{id,jdbcType=BIGINT}
